                    window.sessionState.roleOf.Participant1 && currentSubs.push(window.sessionState.roleOf.Participant1+'_AUDIO');
                    break;
                case 'Actor2':
                    currentSubs=[];
                    window.sessionState.roleOf.Participant1 && currentSubs.push(window.sessionState.roleOf.Participant1);
                    window.sessionState.roleOf.Participant2 && currentSubs.push(window.sessionState.roleOf.Participant2+'_AUDIO');
                    break;
                case 'Attendee':
                    currentSubs=[];
                    break;
                default:
