export default function setSubscriptions(callObject,viewMode=null) {

    if (!window.sessionState) {return;}

    console.log('Setting subscriptions...');
    console.log('****My role is: ' + window.myRole);
    console.log('viewMode is: ' + viewMode);
    let subscribedTo=0;
    window.currentSubs=[];

    //if session is active, set currentSubs according to my role
        if (!viewMode && window.sessionState.isActive) {
            switch (window.myRole) {
                case 'Buyer1':
                case 'Buyer2':
                case 'Buyer3':
                    window.currentSubs=[];
                    window.sessionState.roleOf.Shadow1 && window.currentSubs.push(window.sessionState.roleOf.Shadow1); // see shadow1 if there is a shadow1
                    !window.sessionState.roleOf.Shadow1 && window.sessionState.roleOf.Seller && window.currentSubs.push(window.sessionState.roleOf.Seller); //otherwise, see the original seller

                    break;
                case 'Seller':
                    window.currentSubs=[];
                    window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                    window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                    window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                    break;
                case 'Shadow1':
                case 'Shadow2':
                case 'Shadow3':
                case 'Shadow4':
                    window.currentSubs=[];
                    window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                    window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                    window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                    window.sessionState.roleOf.Seller && window.currentSubs.push(window.sessionState.roleOf.Seller+'_AUDIO');
                    break;
                case 'Attendee':
                    window.currentSubs=[];
                    break;
                default:
                    window.currentSubs=[];
                    break;
            }
            //Add Admin to currentSubs if applicable
            if (window.sessionState.AdminVisible) {
            window.currentSubs.push ("Admin");
        }

        window.currentSubs.filter(function (str) {return str.indexOf(window.myName) === -1}) //don't include self

    }

    if(viewMode) {

        switch (viewMode) {
            case 'Seller':
                window.currentSubs=[];
                window.sessionState.roleOf.Seller && window.currentSubs.push(window.sessionState.roleOf.Seller);
                window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                break;
            case 'Shadow1':
                window.currentSubs=[];
                window.sessionState.roleOf.Shadow1 && window.currentSubs.push(window.sessionState.roleOf.Shadow1);
                window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                break;
            case 'Shadow2':
                window.currentSubs=[];
                window.sessionState.roleOf.Shadow2 && window.currentSubs.push(window.sessionState.roleOf.Shadow2);
                window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                break;
            case 'Shadow3':
                window.sessionState.roleOf.Shadow3 && window.currentSubs.push(window.sessionState.roleOf.Shadow3);
