                window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                break;
            case 'Shadow4':
                window.currentSubs=[];
                window.sessionState.roleOf.Shadow4 && window.currentSubs.push(window.sessionState.roleOf.Shadow4);
                window.sessionState.roleOf.Buyer1 && window.currentSubs.push(window.sessionState.roleOf.Buyer1);
                window.sessionState.roleOf.Buyer2 && window.currentSubs.push(window.sessionState.roleOf.Buyer2);
                window.sessionState.roleOf.Buyer3 && window.currentSubs.push(window.sessionState.roleOf.Buyer3);
                break;
            case 'All':
                window.currentSubs=[];
                window.sessionState.roleOf.Seller && window.currentSubs.push(window.sessionState.roleOf.Seller);
                window.sessionState.roleOf.Shadow1 && window.currentSubs.push(window.sessionState.roleOf.Shadow1);
