import React, { useEffect, useContext, useReducer, useCallback } from 'react';
import './Call.css';
import Tile from '../Tile/Tile';
import CallObjectContext from '../../CallObjectContext';
// import MyInfoContext from '../../MyInfoContext';
// import SessionContext from '../../SessionContext';
import CallMessage from '../CallMessage/CallMessage';
import {
  initialCallState,
  CLICK_ALLOW_TIMEOUT,
  PARTICIPANTS_CHANGE,
  CAM_OR_MIC_ERROR,
  FATAL_ERROR,
  callReducer,
  isLocal,
  isScreenShare,
  containsScreenShare,
  getMessage,
} from './callState';
import { logDailyEvent } from '../../logUtils';
import setSubscriptions from '../../hooks/setSubscriptions';


export default function Call() {
  const callObject = useContext(CallObjectContext);
  // const [ sessionState, setSessionState ] = useContext(SessionContext);
  // const [ myInfo, setMyInfo ] = useContext(MyInfoContext);
  const [callState, dispatch] = useReducer(callReducer, initialCallState);
  let userList=[];
  let adminPresent=false;



  /**
   * Start listening for participant changes, when the callObject is set.
   */
  useEffect(() => {
    if (!callObject) return;

    const events = [
      'participant-joined',
      'participant-updated',
      'participant-left',
    ];



    function handleNewParticipantsState(e) {
      e && logDailyEvent(e);
      dispatch({
        type: PARTICIPANTS_CHANGE,
        participants: callObject.participants(),
      });
      if(e && e.action!='participant-updated') {
        setSubscriptions(callObject);
        console.log("setting subscriptions because: " + e.action);


          let thisUserName = e.participant.user_name;
          let thisSessionID = e.participant.session_id;
          if (e.action=="participant-joined") {
            let SessionIDLabel = thisUserName + "_SessionID";
            window[SessionIDLabel] = thisSessionID;

              userList.push(thisUserName);
              //Add userName to all the dropdowns
              // $("#selectPersonP1").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectPersonP2").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectPersonA1").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectPersonA2").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#audio-target").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom3A").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom3B").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom4A").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom4B").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom5A").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");
              // $("#selectRoom5B").append("<option value=" + thisUserName + ">" + thisUserName + "</option>");

            if (thisUserName=='Admin') {
                adminPresent=true;
            }

          } else if (e.action=="participant-left") {
            //remove from userList
            let index = userList.indexOf(thisUserName);
            if (index > -1) {
            userList.splice(index, 1);
            }
            if (userList.indexOf('Admin')==-1) {
              adminPresent=false;
            }
            //Delete userName from all the dropdowns
            // $("#selectPersonP1 option[value=" + thisUserName + "]").remove();
            // $("#selectPersonP2 option[value=" + thisUserName + "]").remove();
            // $("#selectPersonA1 option[value=" + thisUserName + "]").remove();
            // $("#selectPersonA2 option[value=" + thisUserName + "]").remove();
            // $("#selectRoom3A option[value=" + thisUserName + "]").remove();
            // $("#selectRoom3B option[value=" + thisUserName + "]").remove();
            // $("#selectRoom4A option[value=" + thisUserName + "]").remove();
            // $("#selectRoom4B option[value=" + thisUserName + "]").remove();
            // $("#selectRoom5A option[value=" + thisUserName + "]").remove();
            // $("#selectRoom5B option[value=" + thisUserName + "]").remove();
            // $("#audio-target option[value=" + thisUserName + "]").remove();
          }

          console.log('Admin present: ' + adminPresent);
          console.log('UserList: ' + userList);
      }

    }

    // Use initial state
    handleNewParticipantsState();

    // Listen for changes in state
    for (const event of events) {
      callObject.on(event, handleNewParticipantsState);
    }

    // Stop listening for changes in state
    return function cleanup() {
      for (const event of events) {
        callObject.off(event, handleNewParticipantsState);
      }
    };
  }, [callObject]);

  /**
   * Start listening for call errors, when the callObject is set.
   */
  useEffect(() => {
    if (!callObject) return;

    function handleCameraErrorEvent(event) {
      logDailyEvent(event);
      dispatch({
        type: CAM_OR_MIC_ERROR,
        message:
          (event && event.errorMsg && event.errorMsg.errorMsg) || 'Unknown',
      });
    }

    // We're making an assumption here: there is no camera error when callObject
    // is first assigned.

    callObject.on('camera-error', handleCameraErrorEvent);

    return function cleanup() {
      callObject.off('camera-error', handleCameraErrorEvent);
    };
  }, [callObject]);

  /**
   * Start listening for fatal errors, when the callObject is set.
   */
  useEffect(() => {
    if (!callObject) return;

    function handleErrorEvent(e) {
      logDailyEvent(e);
      dispatch({
        type: FATAL_ERROR,
        message: (e && e.errorMsg) || 'Unknown',
      });
    }

    // We're making an assumption here: there is no error when callObject is
    // first assigned.

    callObject.on('error', handleErrorEvent);

    return function cleanup() {
      callObject.off('error', handleErrorEvent);
    };
  }, [callObject]);

  /**
   * Start a timer to show the "click allow" message, when the component mounts.
   */
  useEffect(() => {
    const t = setTimeout(() => {
      dispatch({ type: CLICK_ALLOW_TIMEOUT });
    }, 2500);

    return function cleanup() {
      clearTimeout(t);
    };
  }, []);



  function getTiles() {
    let largeTiles = [];
    let smallTiles = [];
    Object.entries(callState.callItems).forEach(([id, callItem]) => {

      // if (typeof(currentSubs)=="undefined") {return};
      // let subscribedTracks = [];
      // for (const userName of window.currentSubs) {
      //   const id_number = userName + '_SessionID'
      //   subscribedTracks.push(window[id_number])
      // }
      // console.log (subscribedTracks);
      // console.log("ID: " + id);
      // if (!subscribedTracks.includes(id) && !isLocal(id)) {return};
      // console.log("**MADE IT HERE***");

      const isLarge =
        isScreenShare(id) ||
        (!isLocal(id) && !containsScreenShare(callState.callItems) 
        || isLocal(id) && Object.keys(callState.callItems).length>=3  //////
        );
      const tile = (
        <Tile
          key={id}
          videoTrackState={callItem.videoTrackState}
          audioTrackState={callItem.audioTrackState}
          isLocalPerson={isLocal(id)}
          isLarge={isLarge}
          disableCornerMessage={isScreenShare(id)}
          isMyFirst='true'
          // onClick={
          //   isLocal(id)
          //     ? null
          //     : () => {
          //         sendHello(id);
          //       }
          // }
        />
      );
      if (isLarge) {
        largeTiles.push(tile);
      } else {
        smallTiles.push(tile);
      }
    });

    return [largeTiles, smallTiles];
  }

  const [largeTiles, smallTiles] = getTiles();


  /**
   * DELETE: Attached tiles objects to window for debugging purposes.
   */
  useEffect(() => {
    console.log('re-rendered');
    window.largeTiles = largeTiles;
    window.smallTales = smallTiles;
    window.add = function(n=1) {
      for(let i=0;i<n;i++) {
        callObject.addFakeParticipant()
      }
    };
    window.userList=userList;
    window.adminPresent=adminPresent;
  }); 


   // attach callState to window for debugging
   useEffect(() => {
    window.callState = callState;
  }, [callState]); 


  const message = getMessage(callState);
  return (
    <div className="call">
                                      {/* add # of tiles to class name */}
      <div className={`large-tiles count-${largeTiles.length}`}>   
        {
          !message
            ? largeTiles
            : null /* Avoid showing large tiles to make room for the message */
        }
      </div>
      <div className="small-tiles">{smallTiles}</div>
      {message && (
        <CallMessage
          header={message.header}
          detail={message.detail}
          isError={message.isError}
        />
      )}
    </div>
  );
}
