import React, { useContext, useEffect, useState } from 'react';
import CallObjectContext from '../../CallObjectContext';
import './InputSelector.css';


export default React.forwardRef((props, ref)=> {
const callObject = useContext(CallObjectContext);
const audioInputSelect = React.createRef();
const videoSelect = React.createRef();
const type = props.type;

const handleClick=(e) => {
    let selectionID = e.target.value;
    type == 'audio' && callObject.setInputDevicesAsync({audioSource: selectionID});
    type == 'video' && callObject.setInputDevicesAsync({videoSource: selectionID});
    console.log('New input: ' + e.target.text)
}


   if(navigator.mediaDevices && navigator.mediaDevices.enumerateDevices) {
    navigator.mediaDevices.enumerateDevices()
    .then(function(deviceInfos) {
        for (var i = 0; i !== deviceInfos.length*3; ++i) {
            var deviceInfo = deviceInfos[i];
            var option = document.createElement('option');
            option.className='item';
            option.value = deviceInfo.deviceId;
            if (type == 'audio' && deviceInfo.kind === 'audioinput') {
              option.text = deviceInfo.label ||
                'Microphone ' + (audioInputSelect.length + 1);
                audioInputSelect.current && audioInputSelect.current.appendChild(option);
            // } else if (deviceInfo.kind === 'audiooutput') {
            //   option.text = deviceInfo.label || 'Speaker ' +
            //     (audioOutputSelect.length + 1);
            //   audioOutputSelect.appendChild(option);
            } else if (type == 'video' && deviceInfo.kind === 'videoinput') {
              option.text = deviceInfo.label || 'Camera ' +
                (videoSelect.length + 1);
                videoSelect.current && videoSelect.current.appendChild(option);
            }
      };
    })
    .catch(function(err) {
      console.log(err.name + ": " + err.message);
    });
   } 




    return (
        <div className="dropdown-selector" ref={ref}>
                {type=='audio' &&
                <ul ref={audioInputSelect} onClick={handleClick}>
                </ul>
                }
                {type=='video' &&
                <ul ref={videoSelect} onClick={handleClick}>
                </ul>
                }

        </div>
    );

})
