  const [appState, setAppState] = useState(STATE_IDLE);
  const [roomUrl, setRoomUrl] = useState(null);
  const [callObject, setCallObject] = useState(null);
  const [sessionState, setSessionState] = useState({
                                                    type: 'sessionState',
                                                    isKnown:false,
