import React, { useEffect, useRef, useContext, useState } from 'react';
import './StartScreen.css';
import InputSelector from '../InputSelector/InputSelector';
// import MyInfoContext from '../../MyInfoContext';



//   if (parent.myRole=='Actor') {
//     document.getElementById('instructions').remove();
//     document.getElementById('actor-instructions').style.visibility='visible';
//     let qrCode = '../images/Audio' + parent.myNumber + '.png';
//     document.getElementById('QR-code').src=qrCode;
//   }


/**
 * Props:
 * -
 * -
 */
 export default function StartScreen(props) {

    const userIDEl = useRef(null);
    const [showMicSettings,setShowMicSettings] = useState(false);
    const [showCamSettings,setShowCamSettings] = useState(false);
    const [initialInputs,setInitialInputs] = useState([]);
    const begin = props.onClick;
    // const [ myInfo, setMyInfo ] = useContext(MyInfoContext);
    const [ userID, setUserID ] = useState('Your first name');
    const lobbyVideo = useRef(null);
    const micCaret = React.createRef();
    const micSettings = React.createRef();
    const camCaret = React.createRef();
    const camSettings = React.createRef();
    // const [initialInputs,setInitialInputs] = props.inputs;


    useEffect(()=> {

      document.addEventListener("mousedown", handleClickOutside);


   return function cleanup() {
      document.removeEventListener("mousedown", handleClickOutside);
      };
    })

    const handleClickOutside = (event) => {
      console.log('handling click');
      if (micSettings.current &&
        !micSettings.current.contains(event.target) &&
        !micCaret.current.contains(event.target)
      ) {
        setShowMicSettings(false);
      } else if (camSettings.current &&
        !camSettings.current.contains(event.target) &&
        !camCaret.current.contains(event.target)
      ) {
        setShowCamSettings(false);
      } 

    }



    //   if (parent.myRole.includes('Viewer')) {
//     document.getElementById('welcome-instructions').innerHTML='You are in view-only mode. <br/><br/>Click below to enter. <br/><br/>  <button class="new-card" id="enter-button" onclick="setUserID()">Enter session</button>'
//   }

// useEffect(()=> {
//     if(window.myRole==='Admin') {
//         window.myName='Admin';
//         begin();
//     };
//     },[])

  function setMyName() {
      console.log('setting user ID as ' + userID);
    //   if (parent.myRole.includes('Viewer')) {   //enter session immediately if no need to enter name
    //     begin();
    //     return;
    //   }
      if (userID === '' || userID==='Your first name' || userID.length<=1) {
        alert('Please enter your name to help your partner identify you.')
      } else {
          console.log('***GOT HERE***');
        window.myName=userID;
        begin();
      }
  }
useEffect(()=>{
  navigator.mediaDevices.getUserMedia({audio:true,video:true}).then((mediaStream)=>{
    if(mediaStream){
      window.mediaStream = mediaStream;
      let localAudio = mediaStream.getAudioTracks();
      let localVideo = mediaStream.getVideoTracks();
      setInitialInputs([localAudio[0],localVideo[0]]);
      window.initialInputs=initialInputs;
    }
  })
},[])

useEffect(()=>{
  initialInputs && lobbyVideo.current && (lobbyVideo.current.srcObject=new MediaStream(initialInputs));
},[initialInputs]);


        // if(localAudio && localAudio.length > 0)
        //     {
        //       newCallObject.join({ url, userName:window.myName, audioSource: localSrc[0]});
        //     }
        //     else {}:


  function onFieldChange(value) {
    setUserID(value);
    window.userID=userID;
}


    return (
    <div className='container'>

        <div className='start-screen-background'></div>

        <div className='instructions'> 
            <div className='fixed-width'>

                <h2>Welcome to SecondBody.</h2>



                <div style={{position:'relative', marginLeft:'180px', marginRight:'180px', padding: '20px', border: '4px solid whitesmoke'}}>
                  <div style={{background:'white',opacity:'1',zIndex:'1000'}}>

                      <video autoPlay={true} ref={lobbyVideo} style={{height:'180px',width:'320px',objectFit: 'cover', borderRadius:'20px', background:'white',zIndex:'99'}}></video>

                      {lobbyVideo.current && lobbyVideo.current.srcObject && (


                          <div style={{position:'relative',display:'flex',flexDirection:'row',justifyContent:'center',bottom:'40px', width:'250px',height:'10px',margin:'auto'}}>
                              <div >
                                <p className="selector-button" onClick={()=>{setShowCamSettings(!showCamSettings)}} ref={camCaret}>Select Camera ^</p>
                                    {showCamSettings ? 
                                    (<div onClick={()=>setShowCamSettings(false)}style={{position:'relative',bottom:'0px',left:'80px'}}>
                                      <InputSelector type='video' page='intro' effect={setInitialInputs} ref={camSettings} />
                                    </div>)
                                    : 
                                    null}
                              </div>
                              <div style={{width:'15px'}}></div>

                              <div>
                                <p className="selector-button" onClick={()=>{setShowMicSettings(!showMicSettings)}} ref={micCaret}>Select Mic ^</p>
                                    {showMicSettings ? 
                                      (<div onClick={()=>setShowMicSettings(false)} style={{position:'relative',bottom:'0px',left:'72px',overflowY:'visible'}}>
                                        <InputSelector type='audio' page='intro' ref={micSettings}  effect={setInitialInputs}/>
                                      </div>)
                                    : 
                                    null}
                              </div>
                          </div>
                      )}

                  </div>

                    <h4 id='welcome-instructions'>

                        <form id='form' onSubmit={(e)=>e.preventDefault()}>
                            {/* <label for="userID"></label><br/><br/> */}
                            <input type="text" autoComplete="off" style={{backgroundColor: 'rgb(230, 240, 249)', fontSize:'15px', width: '200px', height: '30px', textAlign:'center', fontFamily:'poppins'}} ref={userIDEl} value={userID} onFocus={()=> {if (userID==='Your first name') {setUserID('')}}} onChange={e => onFieldChange(e.target.value)} /><br/><br/>
                            <input className='new-card' style={{width:'150px', color:'#1b8bc4', fontSize: '17px'}} type="submit" type="submit" value='Enter session' onClick={setMyName} />
                        </form>

                        <br/>
                    </h4>
                </div>


            </div>
        </div>




    </div>
    );
  }
