      getRecordingData().then(data => sendToMux(data.data[0].share_token));


      function getRecordingData() {
        return new Promise((resolve, reject) => {

          let isFinished = false;

          const checkIfUploaded = setInterval(
            ()=>{
                    $.ajax({
