import React from 'react';
import { useLocation } from 'react-router-dom';
import './Header.css';
import { BsFillPeopleFill , BsFillCollectionPlayFill } from "react-icons/bs";

export default function Header(props) {

const [hideHeader, setHideHeader] = props.headerState;

  const location = useLocation();
console.log(location.pathname);


  if(hideHeader) {
    return null;
  } else {
    return (

      <div className='nav-bar'><div className='nav-buttons'>
      <a className='router-button' href='./'><BsFillPeopleFill size='30' style={{margin:'14px'}} /></a>
      <a className='router-button' href='./library'><BsFillCollectionPlayFill size='30' style={{margin:'14px'}} /></a>
      </div></div>
        );
    }


}
