import React, { useEffect, useMemo, useRef } from 'react';
import './Tile.css';

function getTrackUnavailableMessage(kind, trackState) {
  if (!trackState) return;
  switch (trackState.state) {
    case 'blocked':
      if (trackState.blocked.byPermissions) {
        return `${kind} permission denied`;
      } else if (trackState.blocked.byDeviceMissing) {
        return `${kind} device missing`;
      }
      return `${kind} blocked`;
    case 'off':
      if (trackState.off.byUser) {
        return `${kind} muted`;
      } else if (trackState.off.byBandwidth) {
        return `${kind} muted to save bandwidth`;
      }
      return `${kind} off`;
    case 'sendable':
      return `${kind} not subscribed`;
    case 'loading':
      return `${kind} loading...`;
    case 'interrupted':
      return `${kind} interrupted`;
    case 'playable':
      return null;
  }
}

/**
 * Props
 * - videoTrackState: DailyTrackState?
 * - audioTrackState: DailyTrackState?
 * - isLocalPerson: boolean
 * - isAudioOnly: boolean
 * - isLarge: boolean
 * - disableCornerMessage: boolean
 * - onClick: Function
 * -isScreenShare: boolean
 */
export default function Tile(props) {
  const videoEl = useRef(null);
  const audioEl = useRef(null);

  const videoTrack = useMemo(() => {
    return props.videoTrackState && props.videoTrackState.state === 'playable' && (props.isLocalPerson || props.videoTrackState.subscribed === true)
      ? props.videoTrackState.track
      : null;
  }, [props.videoTrackState]);

  const audioTrack = useMemo(() => {

    if (!props.audioTrackState || !props.audioTrackState.track || props.audioTrackState.state !== 'playable' || props.audioTrackState.subscribed === false) 
        {return null;}
    if (props.isAudioOnly) {
      props.audioTrackState.track.isFiltered=true;
    } else {props.audioTrackState.track.isFiltered=false;}
    if (props.isScreenShare) {
      props.audioTrackState.track.isScreenShare=true;
    } else {props.audioTrackState.track.isScreenShare=false;}
    return props.audioTrackState.track
  }, [props.audioTrackState]);

  const videoUnavailableMessage = useMemo(() => {
    return getTrackUnavailableMessage('video', props.videoTrackState);
  }, [props.videoTrackState]);

  const audioUnavailableMessage = useMemo(() => {
    return getTrackUnavailableMessage('audio', props.audioTrackState);
  }, [props.audioTrackState]);

  /**
   * When video track changes, update video srcObject
   */
  useEffect(() => {
    videoEl.current &&
      (videoEl.current.srcObject = new MediaStream([videoTrack]));
  }, [videoTrack]);

  /**
   * When audio track changes, update audio srcObject
   */
  useEffect(() => {
    if(audioEl.current) {

      //create audio stream
      let stream= new MediaStream([audioTrack]);

      //workaround for bug in Chrome, see: https://bit.ly/3ryn1fW
            const mutedAudio = new Audio(); 
            mutedAudio.muted = true;
            mutedAudio.srcObject = stream;
            mutedAudio.play(); 

      //create Audio Context and destination
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      let audioSourceNode = audioCtx.createMediaStreamSource(stream);
      let destination = audioCtx.createMediaStreamDestination();

      //gain Node
      let gainNode = audioCtx.createGain();
      //panner Node
      var panNode = audioCtx.createStereoPanner();
      //splitter




      if(audioTrack.isFiltered) {
        console.log('**FILTERING LIVE TRACK**')
              //adjust nodes
              gainNode.gain.value=1;
              panNode.pan.value=1;      
              //Pipe source through nodes to destination
            audioSourceNode.connect(gainNode).connect(panNode).connect(destination);

      } 
      else if(audioTrack.isScreenShare) {
        const splitterNode = new ChannelSplitterNode(audioCtx, { numberOfOutputs: 2 });
        const splitterNode2 = new ChannelSplitterNode(audioCtx, { numberOfOutputs: 2 });
        const mergerNode = new ChannelMergerNode(audioCtx, { numberOfInputs: 2 });
        const volumeNodeL = new GainNode(audioCtx);
        const volumeNodeR = new GainNode(audioCtx);
        if(window.myRole.includes('Actor')) {   //For actors, mix left channel into both sides and keep right channel
          console.log('**FILTERING SCREENSHARE AUDIO FOR ACTOR**')


        } else {        //For everyone else , mix left channel into both sides and mute right channel
          console.log('**FILTERING SCREENSHARE AUDIO FOR OBSERVER**')

          //split audio source into 2 channels: 0 (left), and 1(right)
          audioSourceNode.connect(splitterNode)


          //adjust channels separately
          splitterNode.connect(volumeNodeL, 0); // connect Left channel to its volume node
          splitterNode.connect(volumeNodeR, 1); // connect Right hcannel to its volume node
          volumeNodeL.gain.value = 1; //set Left volume
          volumeNodeR.gain.value = 0; //set Right volume

          //merge the channels
          volumeNodeL.connect(mergerNode, 0, 0); // Merge left channel
          // volumeNodeR.connect(mergerNode, 0, 1); // Merge right channel
          // volumeNodeR.connect(mergerNode, 0, 0);
        }

          //finally, connect to destination
          mergerNode.connect(destination);

      }
      else {      //For normal live tracks
        console.log('***NOT FILTERING***')
         //Pipe source *straight* to destination
        audioSourceNode.connect(gainNode).connect(destination);

        // how to do this without audiocontext:
        // audioEl.current.srcObject = new MediaStream([audioTrack]);
      }



      //Attach to the audio element
      audioEl.current.srcObject = destination.stream;

      // for debugging
      window.destination=destination; 
      window.audioTrack=audioTrack;
      window.audioEl = audioEl;

      }

  }, [audioTrack,window.sessionState]);

  function getVideoComponent() {
    return videoTrack && <video autoPlay muted playsInline ref={videoEl} />;


  }

  function getAudioComponent() {
    return (
      !props.isLocalPerson &&
      audioTrack && <audio autoPlay playsInline ref={audioEl} > </audio>
    );
  }

  function getOverlayComponent() {
    // Show overlay when video is unavailable. Audio may be unavailable too.
    return videoTrack && (
      videoUnavailableMessage && (
        <p className="overlay">
          {videoUnavailableMessage}
          {/* {audioUnavailableMessage && (
            <>
              <br />
              {audioUnavailableMessage}
            </>
          )} */}
        </p>
      )
    );
  }

  function getCornerMessageComponent() {
    // Show corner message when only audio is unavailable.
    return (
      !props.disableCornerMessage &&
      audioUnavailableMessage &&
      !videoUnavailableMessage && (
        <p className="corner">{audioUnavailableMessage}</p>
      )
    );
  }

  function getClassNames() {
    let classNames = 'tile';
    classNames += props.isLarge ? ' large' : ' small';
    props.isLocalPerson && (classNames += ' local');
    return classNames;
  }

  return (
    ///TODO change to block : none
    <div className={getClassNames()} onClick={props.onClick} style={{display: videoTrack ? "block" : "none"}}>  
      <div className="background"/> 
      {getOverlayComponent()}
      {getVideoComponent()}
      {videoTrack && getCornerMessageComponent()}
      {getAudioComponent()}
    </div>
  );
}
