  useEffect(() => {
    if(audioEl.current) {

      //create audio stream
      window.stream= window.stream || new MediaStream([audioTrack]);

      //workaround for bug in Chrome, see: https://bit.ly/3ryn1fW
            window.mutedAudio = window.mutedAudio || new Audio(); 
