        //   // //mix the left and right into the new right channel
        //   // splitterNodeLL.connect(mergerNodeR, 0, 1); // Merge right of the left channel into right side
        //   // splitterNode.connect(mergerNodeR, 0, 1); // Merge right channel into right side

        //   // //merge far left and new right
        //   // splitterNodeLL.connect(mergerNode, 0, 0);
        //   // mergerNodeR.connect(mergerNode, 0, 1);

        //   audioSourceNode.connect(mergerNode,0,0);
        //   audioSourceNode.connect(mergerNode,0,1);



        // } else {        //For everyone else , mix left channel into both sides and mute right channel
        //   console.log('**FILTERING: SCREENSHARE AUDIO FOR OBSERVER**')

        //   //split audio source into 2 channels: 0 (left), and 1(right)
        //   audioSourceNode.connect(splitterNode)


        //   //adjust channels separately
        //   splitterNode.connect(volumeNodeL, 0); // connect Left channel to its volume node
        //   splitterNode.connect(volumeNodeR, 1); // connect Right channel to its volume node
        //   volumeNodeL.gain.value = 1; //set Left volume
        //   volumeNodeR.gain.value = 1; //set Right volume

        //   //merge the channels
        //   volumeNodeL.connect(mergerNode, 0, 0); // Merge left channel
        //   volumeNodeR.connect(mergerNode, 0, 1); // Merge right channel
        //   // volumeNodeR.connect(mergerNode, 0, 0);
        // }

        //   //finally, connect to destination
        //   mergerNode.connect(destination);

      }
      else {      //For normal live tracks
        console.log('***NOT FILTERING: LIVE TRACK***')
         //Pipe source *straight* to destination
        audioSourceNode.connect(gainNode).connect(destination);

        // how to do this without audiocontext:
        // audioEl.current.srcObject = new MediaStream([audioTrack]);
      }



      //Attach to the audio element
      audioEl.current.srcObject = destination.stream;

      // for debugging
      window.destination=destination; 
      window.audioTrack=audioTrack;
      window.audioEl = audioEl;

      }

  }, [audioTrack,window.sessionState]);

  function getVideoComponent() {
    return videoTrack && <video autoPlay muted playsInline ref={videoEl} />;


  }

  function getAudioComponent() {
    return (
      !props.isLocalPerson &&
      audioTrack && <audio autoPlay playsInline ref={audioEl} > </audio>
    );
  }

  function getOverlayComponent() {
    // Show overlay when video is unavailable. Audio may be unavailable too.
    return videoTrack && (
      videoUnavailableMessage && (
        <p className="overlay">
          {videoUnavailableMessage}
          {/* {audioUnavailableMessage && (
            <>
              <br />
              {audioUnavailableMessage}
            </>
          )} */}
        </p>
      )
