import React, { useEffect, useState } from 'react';
import "../AppLibrary/AppLibrary.css";
import VideoElement from "../VideoElement/VideoElement";
import $ from 'jquery';
import { IoDownloadOutline } from "react-icons/io5";
import { IoIosShareAlt, IoIosTrash } from "react-icons/io";
import Dialogue from "../Dialogue/Dialogue"





export default function AppLibrary() {

  const [videoArray, setVideoArray] = useState([]);
  const [showDeleteDialogue, setShowDeleteDialogue] = useState(false);
  let idToDelete;

  useEffect(()=>{
    $.ajax({
      url: "https://api.mux.com/video/v1/assets",
      beforeSend: function(xhr) { 
      xhr.setRequestHeader("Authorization", "Basic " + btoa("768959a2-7b04-44c2-b584-5800ffc71297:7fB9Fa8rXoCKI3MNDloDusslbS6gwZMoHj6dZldFBNoFEhUTT/pr+c/aI+4S5kImvDtCrBkk14a")); //testing
        // xhr.setRequestHeader("Authorization", "Basic " + btoa("4100efdb-e648-4287-ad0a-50e9875a238b:TyiPEfPzEcz/tl/0sYh5ndPpNIwaom0mQaLRiDQ4E+pX1nH1Xl9XDJWXiPYtgx5QRfi/8ukakFh"));  //livestorm

      },
      type: 'GET',
      contentType: 'application/json',
      success: function (data) {
        // alert(JSON.stringify(data));
        window.test=data.data;
        setVideoArray(data.data);
      },
      error: function(){
        alert("Cannot get recordings");
      }
  });
  },[])

  const deleteVideo = ()=> {
    setShowDeleteDialogue(false);
    alert('DELETED VIDEO');
    window.location.reload();
  }




  return (


    <div>
      <h1>Video library</h1>
      <div className='gallery'>

          {videoArray.map(item => 
            <div >
              <VideoElement src={'https://stream.mux.com/'+item.playback_ids[0].id+'.m3u8'}/>
              <p>{(new Date(parseInt(item.created_at*1000))).toLocaleDateString("en-US", { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' }).toString()}
              <br /> 
              {/* <a href={'https://stream.mux.com/'+item.playback_ids[0].id+'/medium.mp4'} download="FileName">
                <IoDownloadOutline size={20} title="Download" style={{cursor:"pointer",marginTop:"10px"}}/> 
              </a>
              &nbsp; &nbsp; &nbsp;  */}
              <IoIosShareAlt size={20} title="Share" onClick={()=>{alert('share')}} style={{cursor:"pointer",marginTop:"10px"}} />
              &nbsp; &nbsp; &nbsp; 
              <IoIosTrash size={20} title="Delete" onClick={()=>{idToDelete= item.playback_ids[0];setShowDeleteDialogue(true)}} style={{cursor:"pointer" }} />
              </p> 
            </div>
          )}
        <Dialogue 
        show={showDeleteDialogue}
        title="Delete video?"
        description={"Are you sure you wish to permanently delete this video? " + item.playback_ids[0]}
        confirm={deleteVideo}
        cancel={()=>setShowDeleteDialogue(false)}
        />

      </div>
    </div>


  );
}
